function isHttpOk(obj) {
  if (!obj || typeof obj !== 'object') return false;
  // n8n HTTP Request node (continueOnFail) often returns { error: {...} }
  if (obj.error) return false;
  return true;
}

function safeGet(nodeName) {
  try {
    const items = $items(nodeName);
    return items && items[0] ? items[0].json : null;
  } catch (e) {
    return null;
  }
}

const dataapi = safeGet('DataAPI Health');
const agentx = safeGet('AgentX Health');
const ollama99 = safeGet('Ollama 99 Tags');
const ollama12 = safeGet('Ollama 12 Tags');

const dataapiOk = isHttpOk(dataapi) && (dataapi.status === 'ok' || dataapi.status === 'success' || dataapi.ok === true);
const agentxOk = isHttpOk(agentx) && (agentx.status === 'ok' || agentx.status === 'success' || agentx.ok === true);
const ollama99Ok = isHttpOk(ollama99) && (Array.isArray(ollama99.models) || typeof ollama99.models === 'object');
const ollama12Ok = isHttpOk(ollama12) && (Array.isArray(ollama12.models) || typeof ollama12.models === 'object');

const results = {
  timestamp: new Date().toISOString(),
  overall: (dataapiOk && agentxOk && ollama99Ok && ollama12Ok) ? 'healthy' : 'degraded',
  alert: !(dataapiOk && agentxOk && ollama99Ok && ollama12Ok),
  components: {
    dataapi: {
      status: dataapiOk ? 'ok' : 'error',
      detail: dataapiOk ? dataapi : (dataapi && dataapi.error ? dataapi.error : dataapi)
    },
    agentx: {
      status: agentxOk ? 'ok' : 'error',
      detail: agentxOk ? agentx : (agentx && agentx.error ? agentx.error : agentx)
    },
    ollama_99: {
      status: ollama99Ok ? 'ok' : 'error',
      modelsCount: ollama99Ok && Array.isArray(ollama99.models) ? ollama99.models.length : undefined,
      detail: ollama99Ok ? { models: ollama99.models } : (ollama99 && ollama99.error ? ollama99.error : ollama99)
    },
    ollama_12: {
      status: ollama12Ok ? 'ok' : 'error',
      modelsCount: ollama12Ok && Array.isArray(ollama12.models) ? ollama12.models.length : undefined,
      detail: ollama12Ok ? { models: ollama12.models } : (ollama12 && ollama12.error ? ollama12.error : ollama12)
    }
  }
};

return [{ json: results }];

