function isHttpOk(obj) {
  if (!obj || typeof obj !== 'object') return false;
  if (obj.error) return false;
  return true;
}

function safeGet(nodeName) {
  try {
    const items = $items(nodeName);
    return items && items[0] ? items[0].json : null;
  } catch (e) {
    return null;
  }
}

function extractStatusCode(data) {
  if (typeof data?.statusCode === 'number') return data.statusCode;
  if (typeof data?.status === 'number') return data.status;
  if (typeof data?.code === 'number') return data.code;
  if (data?.error) {
    if (typeof data.error.statusCode === 'number') return data.error.statusCode;
    if (typeof data.error.code === 'number') return data.error.code;
  }
  return null;
}

function extractResponseTime(data) {
  const rt = data?.timings?.response ?? data?.elapsedTime ?? data?.responseTime;
  return typeof rt === 'number' ? rt : null;
}

function isApiOk(data) {
  return isHttpOk(data) && (data?.status === 'ok' || data?.status === 'success' || data?.ok === true);
}

function isOllamaOk(data) {
  return isHttpOk(data) && (Array.isArray(data?.models) || typeof data?.models === 'object');
}

const staticData = $getWorkflowStaticData('global');
const previous = staticData.lastStates || {};
const timestamp = new Date().toISOString();
const agentxUrl = $json.agentx_url || 'http://localhost:3080';

const components = [
  { key: 'dataapi', node: 'DataAPI Health', component: 'dataapi', okCheck: isApiOk },
  { key: 'agentx', node: 'AgentX Health', component: 'agentx', okCheck: isApiOk },
  { key: 'ollama99', node: 'Ollama 99 Tags', component: 'ollama_99', okCheck: isOllamaOk },
  { key: 'ollama12', node: 'Ollama 12 Tags', component: 'ollama_12', okCheck: isOllamaOk }
];

const results = [];
const updatedStates = {};

for (const entry of components) {
  const nodeData = safeGet(entry.node);
  const statusCode = extractStatusCode(nodeData);
  const responseTime = extractResponseTime(nodeData);
  const isOk = entry.okCheck(nodeData);
  const slow = responseTime !== null && responseTime > 2000;
  const value = (!isOk || slow) ? 1 : 0;
  const previousValue = previous[entry.key]?.value;
  updatedStates[entry.key] = { value, statusCode, responseTime };

  const changed = previousValue === undefined ? true : previousValue !== value;
  if (changed) {
    results.push({
      json: {
        component: entry.component,
        metric: 'health_status',
        value,
        metadata: {
          responseTime,
          statusCode,
          timestamp
        },
        agentx_url: agentxUrl
      }
    });
  }
}

staticData.lastStates = updatedStates;

return results;

const items = $input.all();

for (const item of items) {
  const payload = item.json?.body ?? item.json;
  const statusCode = payload?.statusCode ?? item.json?.statusCode ?? item.json?.error?.statusCode;
  if (item.json?.error || (typeof statusCode === 'number' && statusCode >= 400)) {
    console.error('Alert evaluation request failed', item.json);
  }
}

return items;
