// Extract aggregation statistics
const data = $input.first().json;

const stats = {
  status: data.status,
  recordsAggregated: data.data?.recordsAggregated || 0,
  timeTaken: data.data?.timeTaken || 0,
  components: data.data?.components || [],
  granularity: data.data?.granularity || 'hourly',
  timestamp: new Date().toISOString()
};

// Log to console
console.log('✅ Metrics Aggregation Successful');
console.log(`Records Aggregated: ${stats.recordsAggregated}`);
console.log(`Time Taken: ${stats.timeTaken}ms`);
console.log(`Components: ${stats.components.join(', ')}`);
console.log(`Timestamp: ${stats.timestamp}`);

// Return stats for further processing
return [{
  json: {
    success: true,
    ...stats,
    message: `Successfully aggregated ${stats.recordsAggregated} records in ${stats.timeTaken}ms`
  }
}];

