// Data Quality Check Function
// Verifies that sufficient metrics were collected in the last hour

const aggregationData = $input.first().json;
const recordsAggregated = aggregationData.recordsAggregated || 0;
const components = aggregationData.components || [];

// Expected minimum records per component per hour
// Assuming health checks every 5 minutes = 12 checks per hour per component
const EXPECTED_MIN_RECORDS_PER_COMPONENT = 12;
const TOTAL_COMPONENTS = 4; // agentx, dataapi, ollama-main, ollama-secondary
const EXPECTED_MIN_TOTAL = EXPECTED_MIN_RECORDS_PER_COMPONENT * TOTAL_COMPONENTS;

// Calculate actual records per component (if available)
const recordsPerComponent = recordsAggregated / TOTAL_COMPONENTS;

// Quality check results
const qualityCheck = {
  passed: recordsAggregated >= EXPECTED_MIN_TOTAL,
  recordsAggregated: recordsAggregated,
  expectedMinimum: EXPECTED_MIN_TOTAL,
  recordsPerComponent: Math.round(recordsPerComponent),
  expectedPerComponent: EXPECTED_MIN_RECORDS_PER_COMPONENT,
  componentsChecked: TOTAL_COMPONENTS,
  timestamp: new Date().toISOString(),
  severity: 'info'
};

// Determine severity based on deficit
if (!qualityCheck.passed) {
  const deficit = EXPECTED_MIN_TOTAL - recordsAggregated;
  const deficitPercent = (deficit / EXPECTED_MIN_TOTAL) * 100;

  if (deficitPercent > 50) {
    qualityCheck.severity = 'critical';
    qualityCheck.message = `Critical: Only ${recordsAggregated} records aggregated (${deficitPercent.toFixed(1)}% below expected)`;
  } else if (deficitPercent > 25) {
    qualityCheck.severity = 'warning';
    qualityCheck.message = `Warning: ${recordsAggregated} records aggregated (${deficitPercent.toFixed(1)}% below expected)`;
  } else {
    qualityCheck.severity = 'low';
    qualityCheck.message = `Low concern: ${recordsAggregated} records aggregated (${deficitPercent.toFixed(1)}% below expected)`;
  }

  console.log('⚠️  Data Quality Check Failed');
  console.log(qualityCheck.message);
  console.log(`Expected: ${EXPECTED_MIN_TOTAL}, Got: ${recordsAggregated}`);
} else {
  qualityCheck.message = `Data quality check passed: ${recordsAggregated} records aggregated`;
  console.log('✅ Data Quality Check Passed');
  console.log(`Records: ${recordsAggregated} (${recordsPerComponent.toFixed(1)} per component)`);
}

return [{
  json: qualityCheck
}];

